var oDiv = document.createElement('ctrl1');
oDiv.innerHTML = '<div id="ctrl2" class="ctrl2" ecui="type:control;id:ctrl2"></div>';
ecui.init(oDiv);

var control = ecui.get("ctrl");
control.onfocus = function () {
  ecui.intercept(control);
}

ecui.get("ctrl1").onclick = function() {
  //打开遮罩层
  ecui.mask(0.5);
}
ecui.get("ctrl2").onclick = function() {
  //关闭遮罩层
  ecui.mask();
}

//点击控件触发控件本身的缩放
ecui.get("ctrl1").onmousedown = function(event) {
  ecui.zoom(this, event);
}

